//import logo from './logo.svg';
//import './App.css';
import React from 'react';
import ReactDOM from 'react-dom';
import Paragraph from './Paragraph';
import {Button, string} from './Button';
import {movies} from './movies';

function App() {

const [currentMovie, setCurrentMovie] = React.useState(null);

const movieStyle = {
backgroundColor: '#F2EDEB',
    height: '250px',
    width: '250px',
    margin: '25px',
    padding: '5px',
    boxShadow: '4px 4px 12px #9A8B84',
    textAlign: 'center',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center'
};

 const mystyle = {
      color: "white",
      backgroundColor: "rebeccapurple",
      padding: "10px",
      fontFamily: "Arial",
     textAlign: "center"
    };
   const [count, setCount] = React.useState(0);
    return (
        <div>
<h1>Movie Information</h1>
        {movies.map( (movie) => {
		return (
		<button key={movie.title} style={movieStyle} onClick={() => {setCurrentMovie(movie)}}>
		<p style={{backgroundColor: 'cornflowerblue', color: 'aliceblue'}}>{movie.title}</p> 
		<p style={{backgroundColor: 'aliceblue', color: 'navy'}}>{movie.rating}</p> 
		<p style={{backgroundColor: 'cornflowerblue', color: 'aliceblue'}}>{movie.director}</p>
		<p style={{backgroundColor: 'aliceblue', color: 'navy'}}>{movie.releaseYear}</p>  
		</button>
		)
	}) }
	{
		currentMovie ?
	<div style={mystyle}>
	<p>{currentMovie.title}</p>
	<img src={currentMovie.posterUrl}/>
	<p>{currentMovie.rating}</p>
	<p>{currentMovie.director}</p>
	<p>{currentMovie.releaseYear}</p>
	<p>{currentMovie.description}</p>
	<p>{currentMovie.categories.join(', ')}</p>
	</div>
	:
	<p>No movie selected.</p>
	}

<Paragraph text={'Phillepe'}/>
<Paragraph text={'Athos'}/>
<Paragraph text={'Porthos'}/>
<Paragraph text={'Aremus'}/>
<Button buttonText={'Greetings!'} onClickFunction={() => alert(string)}/>
 <p>{count}</p>

           <Button buttonText={'Reset'} onClickFunction={() => setCount(0)}/>
     <Button buttonText={'Click to Add'} onClickFunction={() => setCount(count + 1)}/>
     <Button buttonText={'Click to Subtract'} onClickFunction={() => setCount(count - 1)}/>
     </div>
  );

return (
<div>
<div id='root'></div>
</div>
);

}

export default App;
