//import logo from './logo.svg';
//import './App.css';
import React, {Fragment} from 'react';
import ReactDOM from 'react-dom';
import Paragraph from './Paragraph';
import {Button, string} from './Button';
import {movies} from './movies';

function App() {
   const [count, setCount] = React.useState(0);
   var image = movies[0].posterUrl;
   const setImage1 = React.createElement('img', {src: image});
   const setTitle1 = React.createElement('h1', {}, movies[0].title);
   const setRating1 = React.createElement('p', {}, movies[0].rating);
   const setDesc1 = React.createElement('p', {}, movies[0].description);
   const setYear1 = React.createElement('p', {}, movies[0].releaseYear);
   const setDirect1 = React.createElement('p', {}, movies[0].director);
   const setCat1 = React.createElement('p', {}, ' '+ movies[1].categories);
   const setMovie = React.createElement('div', {}, setTitle1, setImage1, setRating1, setDesc1, setDirect1, setYear1, setCat1);



  return (
        <div>
    <div id='root' style='textAlign: center'></div>
<h1>Movie Information</h1>
    {movies.map( (movie) => {
		return (
		<Fragment key={movie.title}>
		<p style={{backgroundColor: 'cornflowerblue', color: 'aliceblue'}}>{movie.title}</p> 
		<p style={{backgroundColor: 'aliceblue', color: 'navy'}}>{movie.rating}</p> 
		<p style={{backgroundColor: 'cornflowerblue', color: 'aliceblue'}}>{movie.director}</p>
		<p style={{backgroundColor: 'aliceblue', color: 'navy'}}>{movie.releaseYear}</p>  
		</Fragment>
		)
	}) }
<Button buttonText={'See Movie'} onClickFunction={() => ReactDOM.render(setMovie, document.getElementById('root'))

}/>
<Paragraph text={'Phillepe'}/>
<Paragraph text={'Athos'}/>
<Paragraph text={'Porthos'}/>
<Paragraph text={'Aremus'}/>
<Button buttonText={'Greetings!'} onClickFunction={() => alert(string)}/>
 <p>{count}</p>

           <Button buttonText={'Reset'} onClickFunction={() => setCount(0)}/>
     <Button buttonText={'Click to Add'} onClickFunction={() => setCount(count + 1)}/>
     <Button buttonText={'Click to Subtract'} onClickFunction={() => setCount(count - 1)}/>
     </div>
  );
}

export default App;
